              <tr style={{ background: '#f3f4f6' }}>
                <th style={{ padding: '12px', textAlign: 'left' }}>Payment Period</th>
                <th style={{ padding: '12px', textAlign: 'left' }}>Hourly Rate</th>
                <th style={{ padding: '12px', textAlign: 'left', position: 'relative' }}>
                  Finished Classes Balance
                </th>
                <th style={{ padding: '12px', textAlign: 'left', position: 'relative' }}>
                  Unfinished Classes Balance
