const BillingDetails = () => {
  const [billing, setBilling] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchBilling = async () => {
      try {
        const response = await axios.get('http://localhost:8081/api/billing/details');
        setBilling(response.data);
      } catch (err) {
        setError('Failed to fetch billing details');
      } finally {
        setLoading(false);
      }
    };
    fetchBilling();
  }, []);

  return (
    <div style={{ maxWidth: '900px', margin: '40px auto', background: '#fff', borderRadius: '16px', boxShadow: '0 2px 16px rgba(0,0,0,0.08)', padding: '32px' }}>
      <h2 style={{ textAlign: 'center', fontWeight: 'bold', fontSize: '2rem', marginBottom: '32px' }}>Billing Details</h2>
      {loading ? (
        <div>Loading...</div>
      ) : error ? (
        <div style={{ color: 'red' }}>{error}</div>
      ) : billing ? (
        <div>
          <div style={{ marginBottom: '24px' }}>
            <strong>Invoice #:</strong> {billing.invoiceNumber}<br />
            <strong>Status:</strong> {billing.status}<br />
            <strong>Date:</strong> {billing.date}
          </div>
          <table style={{ width: '100%', borderCollapse: 'collapse', fontFamily: 'Open Sans, Lato, sans-serif', marginBottom: '24px' }}>
            <thead>
              <tr style={{ background: '#f3f4f6' }}>
                <th style={{ textAlign: 'left', padding: '12px', fontWeight: 'bold' }}>Item</th>
                <th style={{ textAlign: 'left', padding: '12px', fontWeight: 'bold' }}>Amount</th>
              </tr>
            </thead>
            <tbody>
              {billing.items.map((item, idx) => (
                <tr key={idx} style={{ background: idx % 2 === 0 ? '#fff' : '#f9fafb' }}>
                  <td style={{ padding: '12px' }}>{item.name}</td>
                  <td style={{ padding: '12px' }}>${item.amount}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <div style={{ fontWeight: 'bold', fontSize: '1.2rem', textAlign: 'right' }}>
            Total: ${billing.total}
          </div>
        </div>
      ) : null}
    </div>
  );
};

export default BillingDetails;

