import React, { useState } from 'react';

const AddItemForm = ({ onAddItem }) => {
  const [quantity, setQuantity] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!quantity || !description || !price) return;
    onAddItem({ quantity, description, price });
    setQuantity('');
    setDescription('');
    setPrice('');
  };

  return (
    <div style={{ position: 'absolute', bottom: 20, right: 20, background: '#fff', borderRadius: '12px', boxShadow: '0 2px 8px rgba(0,0,0,0.08)', padding: '24px', minWidth: '300px' }}>
      <h3 style={{ marginBottom: '16px', fontWeight: 'bold', fontSize: '1.2rem' }}>ADD ITEMS &gt;</h3>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '12px' }}>
          <input
            type="number"
            placeholder="Quantity"
            value={quantity}
            onChange={e => setQuantity(e.target.value)}
            style={{ width: '100%', padding: '8px', borderRadius: '8px', border: '1px solid #ccc', marginBottom: '8px' }}
          />
          <input
            type="text"
            placeholder="Description"
            value={description}
            onChange={e => setDescription(e.target.value)}
            style={{ width: '100%', padding: '8px', borderRadius: '8px', border: '1px solid #ccc', marginBottom: '8px' }}
          />
          <input
            type="number"
            placeholder="Price"
            value={price}
            onChange={e => setPrice(e.target.value)}
            style={{ width: '100%', padding: '8px', borderRadius: '8px', border: '1px solid #ccc' }}
          />
        </div>
        <button type="submit" style={{ background: '#14b8a6', color: '#fff', border: 'none', borderRadius: '8px', padding: '10px 24px', fontWeight: 'bold', cursor: 'pointer', width: '100%' }}>
          ADD
        </button>
      </form>
    </div>
  );
};

export default AddItemForm;

