import React from 'react';

const Customer = () => {
  // Add logic to fetch and display customer data from backend
  return (
    <div>
      {/* Customer page UI goes here */}
    </div>
  );
};

export default Customer;

import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Customer = () => {
  const [customers, setCustomers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCustomers = async () => {
      try {
        const response = await axios.get('http://localhost:8081/api/customers');
        setCustomers(response.data);
      } catch (err) {
        setError('Failed to fetch customers');
      } finally {
        setLoading(false);
      }
    };
    fetchCustomers();
  }, []);

  return (
    <div style={{ maxWidth: '900px', margin: '40px auto', background: '#fff', borderRadius: '16px', boxShadow: '0 2px 16px rgba(0,0,0,0.08)', padding: '32px' }}>
      <h2 style={{ textAlign: 'center', fontWeight: 'bold', fontSize: '2rem', marginBottom: '32px' }}>Customer List</h2>
      {loading ? (
        <div>Loading...</div>
      ) : error ? (
        <div style={{ color: 'red' }}>{error}</div>
      ) : (
        <table style={{ width: '100%', borderCollapse: 'collapse', fontFamily: 'Open Sans, Lato, sans-serif' }}>
          <thead>
            <tr style={{ background: '#f3f4f6' }}>
              <th style={{ textAlign: 'left', padding: '12px', fontWeight: 'bold' }}>Customer Name</th>
              <th style={{ textAlign: 'left', padding: '12px', fontWeight: 'bold' }}>Contact Info</th>
              <th style={{ textAlign: 'left', padding: '12px', fontWeight: 'bold' }}>Address</th>
            </tr>
          </thead>
          <tbody>
            {customers.map((customer, idx) => (
              <tr key={customer.id} style={{ background: idx % 2 === 0 ? '#fff' : '#f9fafb' }}>
                <td style={{ padding: '12px' }}>{customer.customerName}</td>
                <td style={{ padding: '12px' }}>{customer.contactInfo}</td>
                <td style={{ padding: '12px' }}>{customer.address}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Customer;

